package LoopingStatement_Day5;

public class PracticeLoopStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int b=40;
		
		//while (b!=40)
			
		//	System.out.println(b);
				

		while (b<60)
		{
			System.out.println(b);
			b+=1;
		}
		
		while (b>30)
			
		{
			System.out.println(b);
			b*=10;
		}
		
		b=10;
		
		while (b>0)
			
		{
			System.out.println(b*2);
			b/=2;
		}
		
	}

}
